import React from 'react';

const Header = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header className="header">
      <div className="container">
        <nav className="nav">
          <div className="logo">СТУДІЯ</div>
          <ul className="nav-links">
            <li><a href="#home" onClick={() => scrollToSection('home')}>Головна</a></li>
            <li><a href="#services" onClick={() => scrollToSection('services')}>Послуги</a></li>
            <li><a href="#contact" onClick={() => scrollToSection('contact')}>Контакти</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
