import React from 'react';
import ScrollReveal from './ScrollReveal';

const Services = () => {
  const services = [
    {
      icon: "üåê",
      title: "–í–µ–±-—Ä–æ–∑—Ä–æ–±–∫–∞",
      description: "–°—Ç–≤–æ—Ä–µ–Ω–Ω—è —Å—É—á–∞—Å–Ω–∏—Ö, –∞–¥–∞–ø—Ç–∏–≤–Ω–∏—Ö –≤–µ–±-—Å–∞–π—Ç—ñ–≤ –∑ –≤–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è–º –Ω–∞–π–Ω–æ–≤—ñ—à–∏—Ö —Ç–µ—Ö–Ω–æ–ª–æ–≥—ñ–π. –í—ñ–¥ –ª–µ–Ω–¥—ñ–Ω–≥—ñ–≤ –¥–æ —Å–∫–ª–∞–¥–Ω–∏—Ö –≤–µ–±-–¥–æ–¥–∞—Ç–∫—ñ–≤ –∑ –ø–æ–≤–Ω–∏–º —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª–æ–º."
    },
    {
