    },
    {
      icon: "üé®",
      title: "UI/UX –î–∏–∑–∞–π–Ω",
      description: "–°—Ç–≤–æ—Ä–µ–Ω–Ω—è —ñ–Ω—Ç—É—ó—Ç–∏–≤–Ω–æ–≥–æ —Ç–∞ –ø—Ä–∏–≤–∞–±–ª–∏–≤–æ–≥–æ –∫–æ—Ä–∏—Å—Ç—É–≤–∞—Ü—å–∫–æ–≥–æ —ñ–Ω—Ç–µ—Ä—Ñ–µ–π—Å—É. –ü—Ä–æ—Ç–æ—Ç–∏–ø—É–≤–∞–Ω–Ω—è, —Ç–µ—Å—Ç—É–≤–∞–Ω–Ω—è —Ç–∞ –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è –∫–æ—Ä–∏—Å—Ç—É–≤–∞—Ü—å–∫–æ–≥–æ –¥–æ—Å–≤—ñ–¥—É."
    },
    {
      icon: "üõí",
      title: "E-commerce",
      description: "–†–æ–∑—Ä–æ–±–∫–∞ —ñ–Ω—Ç–µ—Ä–Ω–µ—Ç-–º–∞–≥–∞–∑–∏–Ω—ñ–≤ –∑ –ø–æ–≤–Ω–∏–º —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª–æ–º. –Ü–Ω—Ç–µ–≥—Ä–∞—Ü—ñ—è –∑ –ø–ª–∞—Ç—ñ–∂–Ω–∏–º–∏ —Å–∏—Å—Ç–µ–º–∞–º–∏, CRM —Ç–∞ —Å–∏—Å—Ç–µ–º–∞–º–∏ —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Ç–æ–≤–∞—Ä–∞–º–∏."
    },
    {
      icon: "üîç",
      title: "SEO –û–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è",
      description: "–ü—ñ–¥–≤–∏—â–µ–Ω–Ω—è –≤–∏–¥–∏–º–æ—Å—Ç—ñ —Å–∞–π—Ç—É –≤ –ø–æ—à—É–∫–æ–≤–∏—Ö —Å–∏—Å—Ç–µ–º–∞—Ö. –¢–µ—Ö–Ω—ñ—á–Ω–∞ –æ–ø—Ç–∏–º—ñ–∑–∞—Ü—ñ—è, –∫–æ–Ω—Ç–µ–Ω—Ç-—Å—Ç—Ä–∞—Ç–µ–≥—ñ—è —Ç–∞ –∞–Ω–∞–ª—ñ—Ç–∏–∫–∞ –¥–ª—è –∑—Ä–æ—Å—Ç–∞–Ω–Ω—è —Ç—Ä–∞—Ñ—ñ–∫—É."
    },
    {
      icon: "üí°",
      title: "–ö–æ–Ω—Å—É–ª—å—Ç–∞—Ü—ñ—ó",
      description: "–ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –∫–æ–Ω—Å—É–ª—å—Ç–∞—Ü—ñ—ó –∑ –ø–∏—Ç–∞–Ω—å —Ç–µ—Ö–Ω–æ–ª–æ–≥—ñ–π, –∞—Ä—Ö—ñ—Ç–µ–∫—Ç—É—Ä–∏ –ø—Ä–æ–µ–∫—Ç—É —Ç–∞ —Å—Ç—Ä–∞—Ç–µ–≥—ñ—ó —Ü–∏—Ñ—Ä–æ–≤–æ—ó —Ç—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü—ñ—ó –≤–∞—à–æ–≥–æ –±—ñ–∑–Ω–µ—Å—É."
    }
  ];

  return (
    <section id="services" className="services">
      <div className="container">
        <ScrollReveal className="reveal">
          <h2 className="section-title">–ù–∞—à—ñ –ü–æ—Å–ª—É–≥–∏</h2>
        </ScrollReveal>
        <div className="services-grid">
          {services.map((service, index) => (
            <ScrollReveal key={index} className="reveal-scale" delay={index * 100}>
              <div className="service-card">
                <div className="service-icon">{service.icon}</div>
                <h3>{service.title}</h3>
                <p>{service.description}</p>
              </div>
            </ScrollReveal>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
