import React from 'react';

const Services = () => {
  const services = [
    {
      title: "Веб-розробка",
      description: "Створення сучасних, адаптивних веб-сайтів з використанням найновіших технологій. Від лендінгів до складних веб-додатків."
    },
    {
      title: "Мобільні додатки",
      description: "Розробка мобільних додатків для iOS та Android. Нативні та крос-платформені рішення для вашого бізнесу."
    },
    {
      title: "UI/UX Дизайн",
      description: "Створення інтуїтивного та привабливого користувацького інтерфейсу. Прототипування та тестування користувацького досвіду."
    },
    {
      title: "E-commerce",
      description: "Розробка інтернет-магазинів з повним функціоналом. Інтеграція з платіжними системами та системами управління."
    },
    {
      title: "SEO Оптимізація",
      description: "Підвищення видимості сайту в пошукових системах. Технічна оптимізація та контент-стратегія."
    },
    {
      title: "Консультації",
      description: "Професійні консультації з питань технологій, архітектури проекту та стратегії цифрової трансформації."
    }
  ];

  return (
    <section id="services" className="services">
      <div className="container">
        <h2 className="section-title">Наші Послуги</h2>
        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card">
              <h3>{service.title}</h3>
              <p>{service.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
